import React, { Component } from 'react';
import './styles.css';
// import { withRouter } from 'react-router';
import { connect } from "react-redux";
// import {checkStatus} from '../auth';
import ModalInfo from './Modal';
import { debounce } from "../utils"
import { onLogout, getPlanets } from "../actions";

class Planets extends Component {

    constructor(props) {
        super(props);
        const { userData } = props;
        this.state = {
            planets: [{ name: 'No Data Found' }],
            searchParam: '',
            filtered: props.data || [{ name: 'No Data Found' }],
            currPage: 1,
            searching: 'no',
            name: userData ? userData[0].name : ""
        }
    }

    static getDerivedStateFromProps = (props, state) => {
        const { loginSuccess, history } = props;
        if (!loginSuccess) {
            history.push("/login")
        }
        return null;
    };

    componentDidMount() {
        this.setState({
            planets: this.props.data,
            filtered: this.props.data || [],
            showInfo: false
        });
    }

    filterList(param) {
        //     // var updatedList = this.props.data.filter(function(item){
        //     //     return item.name.toLowerCase().search(event.target.value.toLowerCase()) !== -1;
        //     // });
        //     //this.setState({filtered:event.currentTarget.value?updatedList:[]});
        // let param = event.target.value.toLowerCase();
        debugger;
        const { dispatch } = this.props;
        //   self.setState(
        //     {
        //       searching:'yes'
        //     }
        //   );
        //   this.props.searchAPlanet(param)

        //   .then(function(res){
        //     self.setState({ 
        //               filtered: self.props.data ,
        //               searchParam: param,
        //               searching: 'no'
        //             });
        //   },function(){
        //     alert('No item found!!');
        //   });

        dispatch(getPlanets({ param }));
    }

    lessItems(event) {
        // if (!event.currentTarget.value) this.setState({ filtered: [] });
    }

    logout = () => {
        const { dispatch } = this.props;
        dispatch(onLogout());
    };

    itemClicked(planet) {
        this.setState({
            showInfo: true,
            planet: planet
        });
    }

    hideInfo() {
        this.setState({
            showInfo: false
        });
    }
    render() {
        const { name } = this.state;

        return (
            <div className="container">
                {this.state.showInfo ? <ModalInfo planet={this.state.planet} hideInfo={this.hideInfo.bind(this)} /> : ''}
                <div className="row">
                    <div className="col-md-12 userinfo">
                        Welcome: {name}<i className="fa fa-sign-out" aria-hidden="true" onClick={this.logout}> Logout </i>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-10 center">
                        <input type="text" className="form-control"
                            placeholder={this.props.data && this.props.data.length > 0 ? 'Search' : 'Loading...'}
                            onChange={(evt) => {
                                const val = evt.target.value;
                                return debounce(this.filterList.bind(this, val), 2000)()
                            }} onBlur={this.lessItems} />
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-10 center planet-list">
                        <div className={`list-group searching-${this.state.searching}`}>
                            {this.state.filtered.length > 0 ?
                                (this.state.filtered.map((val, i) => {
                                    let fontSize = {
                                        fontSize: (50 + Number(val.diameter) / 1e3) + 'px'
                                    };
                                    let boundItemClick = this.itemClicked.bind(this, val);
                                    return <a href="#" className="list-group-item" key={i}
                                        style={fontSize} onClick={boundItemClick}>
                                        {val.name} <i className="fa fa-globe" aria-hidden="true">
                                        </i></a>;
                                })
                                ) : ''}
                        </div>
                    </div>

                </div>
            </div>
        );
    }
}

export default connect((state) => {
    // const userData = state.get
    return {
        userData: state.userData,
        loginSuccess: state.loginSuccess
    }
})(Planets);
