import { AppActions } from "../actions";

const reducer = (state = {}, action) => {
    switch (action.type) {
      case AppActions.POST_LOGIN:
      console.log("payload:", action);
        return { ...state, loading: true };
      default:
        return state;
    }
  };

  export default reducer;
