import axios from "axios";
import { baseAPIResourceURL } from "../utils";

const headers = {
    Accept: "application/json",
    "Content-Type": "application/json"
};


const axiosInstance = axios.create({
    baseAPIResourceURL,
    timeout: 1800000,
    headers
});

export let baseAPIResource = {
    get: (slug) => {
        return axiosInstance.get(slug);
    }
};
