import { put, takeLatest, all, call } from 'redux-saga/effects';
import { AppActions, postLogin } from "../actions";
import { baseAPIResource } from "../utils/apiHelper";
import { createBrowserHistory } from 'history';

const newHistory = createBrowserHistory();

// function* fetchNews() {

//   const json = yield fetch('https://newsapi.org/v1/articles?source=cnn&apiKey=c39a26d9c12f48dba2a5c00e35684ecc')
//     .then(response => response.json());

//   yield put({ type: "NEWS_RECEIVED", json: json.articles || [{ error: json.message }] });
// }

function* checkLogin(action) {
  try {
    const { username, password } = action.payload;
    const response = yield call(baseAPIResource.get, `people/?search=${username}`);
    const results = response.data.results;
    const userData = results.length ? results.filter(user => (user.birth_year == password)) : [];
    const isValidUser = userData.length;

    if(!isValidUser) {
      alert("Invalid credentials");
    } else {
      yield put(postLogin(userData));

      yield call(newHistory.push, "/planets");
    }
    debugger;
  } catch (e) {
    console.log("Login error: ", e);
  }
}

function* actionWatcher() {
  yield takeLatest(AppActions.LOGIN_CLICKED, checkLogin)
}


export default function* rootSaga() {
  yield all([
    actionWatcher(),
  ]);
}
